static int redirect_to_splashpage(struct MHD_Connection *connection, t_client *client, const char *host, const char *url)
{
	char *originurl_raw;
	char *originurl;
	char *query;
	int ret = 0;
	const char *separator = "&";
	char *querystr;
	query = safe_calloc(QUERYMAXLEN);
	if (!query) {
		ret = send_error(connection, 503);
		free(query);
		return ret;
	}
	querystr = safe_calloc(QUERYMAXLEN);
	if (!querystr) {
		ret = send_error(connection, 503);
		free(querystr);
		return ret;
	}
	originurl_raw = safe_calloc(MID_BUF);
	if (!originurl_raw) {
		ret = send_error(connection, 503);
		free(originurl_raw);
		return ret;
	}
	originurl = safe_calloc(CUSTOM_ENC);
	if (!originurl) {
		ret = send_error(connection, 503);
		free(originurl);
		return ret;
	}
	get_query(connection, &query, separator);
	if (!query) {
		debug(LOG_DEBUG, "Unable to get query string - error 503");
		free(query);
		return send_error(connection, 503);
	}
	debug(LOG_DEBUG, "Query string is [ %s ]", query);
	safe_asprintf(&originurl_raw, "http:
	uh_urlencode(originurl, CUSTOM_ENC, originurl_raw, strlen(originurl_raw));
	if (strcmp(url, "/login") == 0) {
		client->cpi_query = safe_strdup(originurl);
		debug(LOG_DEBUG, "RFC8910 request: %s", client->cpi_query);
	} else {
		client->cpi_query = "none";
	}
	debug(LOG_DEBUG, "originurl_raw: %s", originurl_raw);
	debug(LOG_DEBUG, "originurl: %s", originurl);
	querystr=construct_querystring(connection, client, originurl, querystr);
	ret = encode_and_redirect_to_splashpage(connection, client, originurl, querystr);
	free(originurl_raw);
	free(originurl);
	free(query);
	free(querystr);
	return ret;
}

zsetcolor(i_ctx_t * i_ctx_p)
{
    os_ptr                  op = osp;
    es_ptr ep;
    const gs_color_space *  pcs = gs_currentcolorspace(igs);
    gs_client_color         cc;
    int                     n_comps, n_numeric_comps, num_offset = 0, code, depth;
    PS_colour_space_t *space;
    cc.pattern = 0;
    if ((n_comps = cs_num_components(pcs)) < 0) {
        n_comps = -n_comps;
         if (r_has_type(op, t_dictionary)) {
             ref     *pImpl, pPatInst;
            code = dict_find_string(op, "Implementation", &pImpl);
            if (code != 0) {
                 code = array_get(imemory, pImpl, 0, &pPatInst);
                 if (code < 0)
                     return code;
                n_numeric_comps = ( pattern_instance_uses_base_space(cc.pattern)
                      ? n_comps - 1
                      : 0 );
            } else
                n_numeric_comps = 0;
        } else
            n_numeric_comps = 0;
        num_offset = 1;
    } else
        n_numeric_comps = n_comps;
    code = float_params(op - num_offset, n_numeric_comps, cc.paint.values);
    if (code < 0)
        return code;
    memset(cc.paint.values + n_numeric_comps, 0,
            sizeof(cc.paint.values) - sizeof(*cc.paint.values)*n_numeric_comps);
    code = get_space_object(i_ctx_p, &istate->colorspace[0].array, &space);
    if (code < 0)
        return code;
    if (space->validatecomponents) {
        code = space->validatecomponents(i_ctx_p,
                                         &istate->colorspace[0].array,
                                         cc.paint.values, n_numeric_comps);
        if (code < 0)
            return code;
    }
    if ((code = gs_setcolor(igs, &cc)) >= 0) {
        if (n_comps > n_numeric_comps) {
            istate->pattern[0] = *op;      
        }
    }
    code = validate_spaces(i_ctx_p, &istate->colorspace[0].array, &depth);
    if (code < 0)
        return code;
    check_estack(5);
    ep = esp += 1;
    make_int(ep, 0);
    ep = esp += 1;
    make_int(ep, 0);
    ep = esp += 1;
    make_int(ep, 0);
    ep = esp += 1;
    *ep = istate->colorspace[0].array;
    push_op_estack(setcolor_cont);
    return o_push_estack;
}

