std::wstring MB2WC(const std::string& input, unsigned int code_page) {
  if (input.empty()) {
    return L"";
  }
  int length = ::MultiByteToWideChar(code_page, 0, &input[0],
                                     static_cast<int>(input.size()),
                                     NULL, 0);
  std::wstring output(length, '\0');
  ::MultiByteToWideChar(code_page, 0, &input[0], static_cast<int>(input.size()),
                        &output[0], static_cast<int>(output.size()));
  return output;
}

accept_ice_connection (GIOChannel           *source,
                       GIOCondition          condition,
                       GsmIceConnectionData *data)
{
       IceListenObj    listener;
        IceConn         ice_conn;
        IceAcceptStatus status;
       GsmClient      *client;
       GsmXsmpServer  *server;
       listener = data->listener;
       server = data->server;
        g_debug ("GsmXsmpServer: accept_ice_connection()");
       ice_conn = IceAcceptConnection (listener, &status);
        if (status != IceAcceptSuccess) {
                g_debug ("GsmXsmpServer: IceAcceptConnection returned %d", status);
                return TRUE;
        }
       client = gsm_xsmp_client_new (ice_conn);
       ice_conn->context = client;
       gsm_store_add (server->priv->client_store, gsm_client_peek_id (client), G_OBJECT (client));
       g_object_unref (client);
        return TRUE;
}

