static int newary(struct ipc_namespace *ns, struct ipc_params *params)
{
	int id;
	int retval;
	struct sem_array *sma;
	int size;
	key_t key = params->key;
	int nsems = params->u.nsems;
	int semflg = params->flg;
	int i;
	if (!nsems)
		return -EINVAL;
	if (ns->used_sems + nsems > ns->sc_semmns)
		return -ENOSPC;
	size = sizeof (*sma) + nsems * sizeof (struct sem);
	sma = ipc_rcu_alloc(size);
	if (!sma) {
		return -ENOMEM;
	}
	memset (sma, 0, size);
	sma->sem_perm.mode = (semflg & S_IRWXUGO);
	sma->sem_perm.key = key;
	sma->sem_perm.security = NULL;
	retval = security_sem_alloc(sma);
	if (retval) {
		ipc_rcu_putref(sma);
		return retval;
	}
	id = ipc_addid(&sem_ids(ns), &sma->sem_perm, ns->sc_semmni);
	if (id < 0) {
		security_sem_free(sma);
		ipc_rcu_putref(sma);
		return id;
	}
	ns->used_sems += nsems;
	sma->sem_base = (struct sem *) &sma[1];
	for (i = 0; i < nsems; i++)
		INIT_LIST_HEAD(&sma->sem_base[i].sem_pending);
	sma->complex_count = 0;
	INIT_LIST_HEAD(&sma->sem_pending);
	INIT_LIST_HEAD(&sma->list_id);
	sma->sem_nsems = nsems;
	sma->sem_ctime = get_seconds();
	sem_unlock(sma);
	return sma->sem_perm.id;
}

static void mkiss_close(struct tty_struct *tty)
{
	struct mkiss *ax;
	write_lock_irq(&disc_data_lock);
	ax = tty->disc_data;
	tty->disc_data = NULL;
	write_unlock_irq(&disc_data_lock);
	if (!ax)
		return;
	if (!refcount_dec_and_test(&ax->refcnt))
		wait_for_completion(&ax->dead);
	netif_stop_queue(ax->dev);
	kfree(ax->rbuff);
	kfree(ax->xbuff);
	ax->tty = NULL;
	unregister_netdev(ax->dev);
	free_netdev(ax->dev);
}

static TEE_Result do_allocate_publickey(struct dsa_public_key *key,
					size_t l_bits, size_t n_bits)
{
	DSA_TRACE("DSA Allocate Public of L=%zu bits and N=%zu bits", l_bits,
		  n_bits);
	memset(key, 0, sizeof(*key));
	key->g = crypto_bignum_allocate(l_bits);
	if (!key->g)
		goto err;
	key->p = crypto_bignum_allocate(l_bits);
	if (!key->p)
		goto err;
	key->q = crypto_bignum_allocate(n_bits);
	if (!key->q)
		goto err;
	key->y = crypto_bignum_allocate(l_bits);
	if (!key->y)
		goto err;
	return TEE_SUCCESS;
err:
	DSA_TRACE("Allocation error");
	crypto_bignum_free(key->g);
	crypto_bignum_free(key->p);
	crypto_bignum_free(key->q);
	return TEE_ERROR_OUT_OF_MEMORY;
}

static void adjust_branches(struct bpf_prog *prog, int pos, int delta)
{
	struct bpf_insn *insn = prog->insnsi;
	int insn_cnt = prog->len;
	int i;
	for (i = 0; i < insn_cnt; i++, insn++) {
		if (BPF_CLASS(insn->code) != BPF_JMP ||
		    BPF_OP(insn->code) == BPF_CALL ||
		    BPF_OP(insn->code) == BPF_EXIT)
			continue;
 		if (i < pos && i + insn->off + 1 > pos)
 			insn->off += delta;
		else if (i > pos && i + insn->off + 1 < pos)
 			insn->off -= delta;
 	}
 }

