HMAC_SHA256_Final(unsigned char digest[32], struct HMAC_SHA256_CTX * ctx)
{
  unsigned char ihash[32];
  scrypt_SHA256_Final(ihash, &ctx->ictx);
  scrypt_SHA256_Update(&ctx->octx, ihash, 32);
  scrypt_SHA256_Final(digest, &ctx->octx);
  memset(ihash, 0, 32);
}

