void Compute(OpKernelContext* ctx) override {
  auto x = ctx->input(0);
  auto i = ctx->input(1);
  auto v = ctx->input(2);
  OP_REQUIRES(ctx, TensorShapeUtils::IsVector(i.shape()),
              errors::InvalidArgument("i must be a vector. ",
                                      i.shape().DebugString()));
  OP_REQUIRES(ctx, x.dims() == v.dims(),
              errors::InvalidArgument(
                  "x and v shape doesn't match (ranks differ): ",
                  x.shape().DebugString(), " vs. ", v.shape().DebugString()));
  for (int i = 1; i < x.dims(); ++i) {
    OP_REQUIRES(
        ctx, x.dim_size(i) == v.dim_size(i),
        errors::InvalidArgument("x and v shape doesn't match at index ", i,
                                " : ", x.shape().DebugString(), " vs. ",
                                v.shape().DebugString()));
  }
  OP_REQUIRES(ctx, i.dim_size(0) == v.dim_size(0),
              errors::InvalidArgument(
                  "i and x shape doesn't match at index 0: ",
                  i.shape().DebugString(), " vs. ", v.shape().DebugString()));
  Tensor y = x;  
  if (x.NumElements() > 0 || v.NumElements() > 0) {
    OP_REQUIRES_OK(ctx, DoCompute(ctx, i, v, &y));
  }
  ctx->set_output(0, y);
}

windows_icon_typefind (GstTypeFind * find, gpointer user_data)
{
  const guint8 *data;
  gint64 datalen;
  guint16 type, nimages;
  gint32 size, offset;
  datalen = gst_type_find_get_length (find);
  if ((data = gst_type_find_peek (find, 0, 6)) == NULL)
    return;
  if (GST_READ_UINT16_LE (data) != 0)
    return;
  type = GST_READ_UINT16_LE (data + 2);
  if (type != 1 && type != 2)
    return;
  nimages = GST_READ_UINT16_LE (data + 4);
  if (nimages == 0)             
    return;
  if (data[6 + 3] != 0)
    return;
  if (type == 1) {
    guint16 planes = GST_READ_UINT16_LE (data + 6 + 4);
    if (planes > 1)
      return;
  }
  size = GST_READ_UINT32_LE (data + 6 + 8);
  offset = GST_READ_UINT32_LE (data + 6 + 12);
  if (offset < 0 || size <= 0 || size >= datalen || offset >= datalen
      || size + offset > datalen)
    return;
  gst_type_find_suggest_simple (find, GST_TYPE_FIND_NEARLY_CERTAIN,
      "image/x-icon", NULL);
}

static unsigned long __init usemap_size(unsigned long zone_start_pfn, unsigned long zonesize)
{
	unsigned long usemapsize;
	zonesize += zone_start_pfn & (pageblock_nr_pages-1);
	usemapsize = roundup(zonesize, pageblock_nr_pages);
	usemapsize = usemapsize >> pageblock_order;
	usemapsize *= NR_PAGEBLOCK_BITS;
	usemapsize = roundup(usemapsize, 8 * sizeof(unsigned long));
	return usemapsize / 8;
}

