static TEE_Result do_alloc_keypair(struct rsa_keypair *s,
				   size_t key_size_bits __unused)
{
	memset(s, 0, sizeof(*s));
	if (!bn_alloc_max(&s->e))
		return TEE_ERROR_OUT_OF_MEMORY;
	if (!bn_alloc_max(&s->d))
		goto err;
	if (!bn_alloc_max(&s->n))
		goto err;
	if (!bn_alloc_max(&s->p))
		goto err;
	if (!bn_alloc_max(&s->q))
		goto err;
	if (!bn_alloc_max(&s->qp))
		goto err;
	if (!bn_alloc_max(&s->dp))
		goto err;
	if (!bn_alloc_max(&s->dq))
		goto err;
	return TEE_SUCCESS;
err:
	crypto_bignum_free(s->e);
	crypto_bignum_free(s->d);
	crypto_bignum_free(s->n);
	crypto_bignum_free(s->p);
	crypto_bignum_free(s->q);
	crypto_bignum_free(s->qp);
	crypto_bignum_free(s->dp);
	crypto_bignum_free(s->dq);
	return TEE_ERROR_OUT_OF_MEMORY;
}

cff_charset_load( CFF_Charset  charset,
                  FT_UInt      num_glyphs,
                  FT_Stream    stream,
                  FT_ULong     base_offset,
                  FT_ULong     offset,
                  FT_Bool      invert )
{
  FT_Memory  memory = stream->memory;
  FT_Error   error  = CFF_Err_Ok;
  FT_UShort  glyph_sid;
  if ( offset > 2 )
  {
    FT_UInt  j;
    charset->offset = base_offset + offset;
    if ( FT_STREAM_SEEK( charset->offset ) ||
         FT_READ_BYTE( charset->format )   )
      goto Exit;
    if ( FT_NEW_ARRAY( charset->sids, num_glyphs ) )
      goto Exit;
    charset->sids[0] = 0;
    switch ( charset->format )
    {
    case 0:
      if ( num_glyphs > 0 )
      {
        if ( FT_FRAME_ENTER( ( num_glyphs - 1 ) * 2 ) )
           goto Exit;
         for ( j = 1; j < num_glyphs; j++ )
          charset->sids[j] = FT_GET_USHORT();
         FT_FRAME_EXIT();
       }
          if ( FT_READ_USHORT( glyph_sid ) )
            goto Exit;
          if ( charset->format == 2 )
          {
            if ( FT_READ_USHORT( nleft ) )
              goto Exit;
          }
          else
          {
            if ( FT_READ_BYTE( nleft ) )
              goto Exit;
          }
          for ( i = 0; j < num_glyphs && i <= nleft; i++, j++, glyph_sid++ )
            charset->sids[j] = glyph_sid;
        }
      }
      break;
    default:
      FT_ERROR(( "cff_charset_load: invalid table format!\n" ));
      error = CFF_Err_Invalid_File_Format;
               goto Exit;
           }

void spl_filesystem_object_construct(INTERNAL_FUNCTION_PARAMETERS, zend_long ctor_flags) 
{
	spl_filesystem_object *intern;
	char *path;
	int parsed;
	size_t len;
	zend_long flags;
	zend_error_handling error_handling;
	zend_replace_error_handling(EH_THROW, spl_ce_UnexpectedValueException, &error_handling);
	if (SPL_HAS_FLAG(ctor_flags, DIT_CTOR_FLAGS)) {
		flags = SPL_FILE_DIR_KEY_AS_PATHNAME|SPL_FILE_DIR_CURRENT_AS_FILEINFO;
		parsed = zend_parse_parameters(ZEND_NUM_ARGS(), "s|l", &path, &len, &flags);
	} else {
		flags = SPL_FILE_DIR_KEY_AS_PATHNAME|SPL_FILE_DIR_CURRENT_AS_SELF;
		parsed = zend_parse_parameters(ZEND_NUM_ARGS(), "s", &path, &len);
	}
	if (SPL_HAS_FLAG(ctor_flags, SPL_FILE_DIR_SKIPDOTS)) {
		flags |= SPL_FILE_DIR_SKIPDOTS;
	}
	if (SPL_HAS_FLAG(ctor_flags, SPL_FILE_DIR_UNIXPATHS)) {
		flags |= SPL_FILE_DIR_UNIXPATHS;
	}
	if (parsed == FAILURE) {
		zend_restore_error_handling(&error_handling);
		return;
	}
	if (!len) {
		zend_throw_exception_ex(spl_ce_RuntimeException, 0, "Directory name must not be empty.");
		zend_restore_error_handling(&error_handling);
		return;
	}
	intern = Z_SPLFILESYSTEM_P(getThis());
	if (intern->_path) {
		zend_restore_error_handling(&error_handling);
		php_error_docref(NULL, E_WARNING, "Directory object is already initialized");
		return;
	}
	intern->flags = flags;
#ifdef HAVE_GLOB
	if (SPL_HAS_FLAG(ctor_flags, DIT_CTOR_GLOB) && strstr(path, "glob:
		spprintf(&path, 0, "glob:
		spl_filesystem_dir_open(intern, path);
		efree(path);
	} else
#endif
	{
		spl_filesystem_dir_open(intern, path);
	}
	intern->u.dir.is_recursive = instanceof_function(intern->std.ce, spl_ce_RecursiveDirectoryIterator) ? 1 : 0;
	zend_restore_error_handling(&error_handling);
}

