   text[1] = RE_YY_INPUT(yyscanner);
  if (text[1] == EOF)
     return 0;
   if (text[1] == 'x')
   {
     text[2] = RE_YY_INPUT(yyscanner);
    if (text[2] == EOF)
       return 0;
     text[3] = RE_YY_INPUT(yyscanner);
