static int __get_data_block(struct inode *inode, sector_t iblock,
			struct buffer_head *bh, int create, int flag,
			pgoff_t *next_pgofs)
{
	struct f2fs_map_blocks map;
	int err;
	map.m_lblk = iblock;
	map.m_len = bh->b_size >> inode->i_blkbits;
	map.m_next_pgofs = next_pgofs;
	err = f2fs_map_blocks(inode, &map, create, flag);
 	if (!err) {
 		map_bh(bh, inode->i_sb, map.m_pblk);
 		bh->b_state = (bh->b_state & ~F2FS_MAP_FLAGS) | map.m_flags;
		bh->b_size = map.m_len << inode->i_blkbits;
 	}
 	return err;
 }

ImagingPcxDecode(Imaging im, ImagingCodecState state, UINT8* buf, Py_ssize_t bytes)
{
    UINT8 n;
    UINT8* ptr;
    if (strcmp(im->mode, "1") == 0 && state->xsize > state->bytes * 8) {
        state->errcode = IMAGING_CODEC_OVERRUN;
        return -1;
    }
    ptr = buf;
    for (;;) {
	if (bytes < 1)
	    return ptr - buf;
	if ((*ptr & 0xC0) == 0xC0) {
	    if (bytes < 2)
		return ptr - buf;
	    n = ptr[0] & 0x3F;
	    while (n > 0) {
		if (state->x >= state->bytes) {
		    state->errcode = IMAGING_CODEC_OVERRUN;
		    break;
		}
		state->buffer[state->x++] = ptr[1];
		n--;
	    }
	    ptr += 2; bytes -= 2;
	} else {
	    state->buffer[state->x++] = ptr[0];
	    ptr++; bytes--;
	}
	if (state->x >= state->bytes) {
        if (state->bytes % state->xsize && state->bytes > state->xsize) {
            int bands = state->bytes / state->xsize;
            int stride = state->bytes / bands;
            int i;
            for (i=1; i< bands; i++) {  
                memmove(&state->buffer[i*state->xsize],
                        &state->buffer[i*stride],
                        state->xsize);
            }
        }
	    state->shuffle((UINT8*) im->image[state->y + state->yoff] +
			   state->xoff * im->pixelsize, state->buffer,
			   state->xsize);
	    state->x = 0;
	    if (++state->y >= state->ysize) {
		return -1;
	    }
	}
    }
}

int fpm_unix_resolve_socket_premissions(struct fpm_worker_pool_s *wp) 
{
	struct fpm_worker_pool_config_s *c = wp->config;
	wp->socket_uid = -1;
	wp->socket_gid = -1;
	wp->socket_mode = 0666;
	if (!c) {
		return 0;
	}
	if (c->listen_owner && *c->listen_owner) {
		struct passwd *pwd;
		pwd = getpwnam(c->listen_owner);
		if (!pwd) {
			zlog(ZLOG_SYSERROR, "[pool %s] cannot get uid for user '%s'", wp->config->name, c->listen_owner);
			return -1;
		}
		wp->socket_uid = pwd->pw_uid;
		wp->socket_gid = pwd->pw_gid;
	}
	if (c->listen_group && *c->listen_group) {
		struct group *grp;
		grp = getgrnam(c->listen_group);
		if (!grp) {
			zlog(ZLOG_SYSERROR, "[pool %s] cannot get gid for group '%s'", wp->config->name, c->listen_group);
			return -1;
		}
		wp->socket_gid = grp->gr_gid;
	}
	if (c->listen_mode && *c->listen_mode) {
		wp->socket_mode = strtoul(c->listen_mode, 0, 8);
	}
	return 0;
}

