static int __iommu_calculate_agaw(struct intel_iommu *iommu, int max_gaw)
{
	unsigned long sagaw;
	int agaw = -1;
	sagaw = cap_sagaw(iommu->cap);
	for (agaw = width_to_agaw(max_gaw);
	     agaw >= 0; agaw--) {
		if (test_bit(agaw, &sagaw))
			break;
	}
	return agaw;
}

static void icmp_reply(struct icmp_bxm *icmp_param, struct sk_buff *skb)
{
	struct ipcm_cookie ipc;
	struct rtable *rt = skb_rtable(skb);
	struct net *net = dev_net(rt->dst.dev);
	struct sock *sk;
	struct inet_sock *inet;
	__be32 daddr;
	if (ip_options_echo(&icmp_param->replyopts, skb))
		return;
	sk = icmp_xmit_lock(net);
	if (sk == NULL)
		return;
	inet = inet_sk(sk);
	icmp_param->data.icmph.checksum = 0;
	inet->tos = ip_hdr(skb)->tos;
	daddr = ipc.addr = rt->rt_src;
	ipc.opt = NULL;
	ipc.tx_flags = 0;
	if (icmp_param->replyopts.optlen) {
		ipc.opt = &icmp_param->replyopts;
		if (ipc.opt->srr)
			daddr = icmp_param->replyopts.faddr;
	}
	{
		struct flowi4 fl4 = {
			.daddr = daddr,
			.saddr = rt->rt_spec_dst,
			.flowi4_tos = RT_TOS(ip_hdr(skb)->tos),
			.flowi4_proto = IPPROTO_ICMP,
		};
		security_skb_classify_flow(skb, flowi4_to_flowi(&fl4));
		rt = ip_route_output_key(net, &fl4);
		if (IS_ERR(rt))
			goto out_unlock;
	}
	if (icmpv4_xrlim_allow(net, rt, icmp_param->data.icmph.type,
			       icmp_param->data.icmph.code))
		icmp_push_reply(icmp_param, &ipc, &rt);
	ip_rt_put(rt);
out_unlock:
	icmp_xmit_unlock(sk);
}

snmp_oid_encode_oid(unsigned char *out, uint32_t *out_len, uint32_t *oid)
{
  uint32_t original_out_len;
  uint32_t *oid_start = oid;
  uint32_t num;
  original_out_len = *out_len;
  while(*oid != ((uint32_t)-1)) {
    ++oid;
  }
  --oid;
  while(oid != oid_start) {
    num = *oid;
    (*out_len)++;
    *out-- = (uint8_t)(num & 0x7F);
    num >>= 7;
    while(num) {
      (*out_len)++;
      *out-- = (uint8_t)((num & 0x7F) | 0x80);
      num >>= 7;
    }
    --oid;
  }
  num = *(out + 1) + 40 * *oid;
  (*out_len)--;
  out++;
  (*out_len)++;
  *out-- = (uint8_t)(num & 0x7F);
  num >>= 7;
  while(num) {
    (*out_len)++;
    *out-- = (uint8_t)((num & 0x7F) | 0x80);
    num >>= 7;
  }
  out = snmp_ber_encode_length(out, out_len, ((*out_len - original_out_len) & 0xFF));
  out = snmp_ber_encode_type(out, out_len, SNMP_DATA_TYPE_OBJECT);
  return out;
}

