YCPBoolean IniAgent::Write(const YCPPath &path, const YCPValue& value, const YCPValue& arg)
{
    if (!parser.isStarted())
    {
	y2warning("Can't execute Write before being mounted.");
	return YCPBoolean (false);
    }
    bool ok = false; 
    YCPBoolean b (true);
    if (0 == path->length ())
    {
	if (value->isString() && value->asString()->value() == "force")
	    parser.inifile.setDirty();
	else if (value->isString () && value->asString()->value() == "clean")
	    parser.inifile.clean ();
	if (0 != parser.write ())
	    b = false;
	ok = true;
    }
    else
    {
	if (( parser.repeatNames () && value->isList ()) ||
	    (!parser.repeatNames () &&  (value->isString () || value->isInteger())) ||
	    path->component_str(0) == "all"
	    )
	    {
		ok = true;
		if (parser.inifile.Write (path, value, parser.HaveRewrites ()))
		    b = false;
	    }
        else if (value->isVoid ())
	    {
		int wb  = -1;
		string del_sec = "";
		ok = true;
		if (2 == path->length ())
		{
		    string pc = path->component_str(0);
		    if ("s" == pc || "section" == pc)
		    {	
			del_sec = path->component_str (1);
			wb = parser.inifile.getSubSectionRewriteBy (del_sec.c_str());
		    }
		}
		if (parser.inifile.Delete (path))
		    b = false;
		else if (del_sec != "")
		{
		    parser.deleted_sections.insert (parser.getFileName (del_sec, wb));
		}
	    }
	else
	{
	    ycp2error ("Wrong value for path %s: %s", path->toString ().c_str (), value->toString ().c_str ());
	    b = false;
	}
    }
    if (!ok)
    {
    	ycp2error ( "Wrong path '%s' in Write().", path->toString().c_str () );
    }
    return b;
}

void Compute(OpKernelContext* context) override {
  const Tensor& data = context->input(0);
  const Tensor& weights = context->input(1);
  bool use_weights = weights.NumElements() > 0;
  OP_REQUIRES(context,
              TensorShapeUtils::IsVector(data.shape()) ||
                  TensorShapeUtils::IsMatrix(data.shape()),
              errors::InvalidArgument(
                  "Input must be a 1 or 2-dimensional tensor. Got: ",
                  data.shape().DebugString()));
  if (use_weights) {
    OP_REQUIRES(
        context, weights.shape() == data.shape(),
        errors::InvalidArgument(
            "Weights and data must have the same shape. Weight shape: ",
            weights.shape().DebugString(),
            "; data shape: ", data.shape().DebugString()));
  }
  bool is_1d = TensorShapeUtils::IsVector(data.shape());
  int negative_valued_axis = -1;
  int num_batch_dimensions = (data.shape().dims() + negative_valued_axis);
  int num_batch_elements = 1;
  for (int i = 0; i < num_batch_dimensions; ++i) {
    num_batch_elements *= data.shape().dim_size(i);
  }
  int num_value_elements = data.shape().num_elements() / num_batch_elements;
  auto per_batch_counts = BatchedMap<W>(num_batch_elements);
  T max_value = 0;
  const auto data_values = data.flat<T>();
  const auto weight_values = weights.flat<W>();
  int i = 0;
  for (int b = 0; b < num_batch_elements; ++b) {
    for (int v = 0; v < num_value_elements; ++v) {
      const auto& value = data_values(i);
      if (value >= 0 && (maxlength_ <= 0 || value < maxlength_)) {
        if (binary_output_) {
          per_batch_counts[b][value] = 1;
        } else if (use_weights) {
          per_batch_counts[b][value] += weight_values(i);
        } else {
          per_batch_counts[b][value]++;
        }
        if (value > max_value) {
          max_value = value;
        }
      }
      ++i;
    }
  }
  int num_output_values = GetOutputSize(max_value, maxlength_, minlength_);
  OP_REQUIRES_OK(context, OutputSparse<W>(per_batch_counts, num_output_values,
                                          is_1d, context));
}

u32 gf_isom_get_nalu_length_field(GF_ISOFile *file, u32 track, u32 StreamDescriptionIndex)
{
	GF_TrackBox *trak;
	GF_SampleEntryBox *entry;
	GF_MPEGVisualSampleEntryBox *ve;
	GF_SampleDescriptionBox *stsd;
	trak = gf_isom_get_track_from_file(file, track);
	if (!trak) {
		file->LastError = GF_BAD_PARAM;
		return 0;
	}
	stsd = trak->Media->information->sampleTable->SampleDescription;
	if (!stsd || !StreamDescriptionIndex || StreamDescriptionIndex > gf_list_count(stsd->child_boxes)) {
		file->LastError = GF_BAD_PARAM;
		return 0;
	}
	entry = (GF_SampleEntryBox *)gf_list_get(stsd->child_boxes, StreamDescriptionIndex - 1);
	if (!entry || ! gf_isom_is_nalu_based_entry(trak->Media, entry)) {
		file->LastError = GF_BAD_PARAM;
		return 0;
	}
	ve = (GF_MPEGVisualSampleEntryBox*)entry;
	if (ve->avc_config) return ve->avc_config->config->nal_unit_size;
	if (ve->svc_config) return ve->svc_config->config->nal_unit_size;
	if (ve->hevc_config) return ve->hevc_config->config->nal_unit_size;
	if (ve->lhvc_config) return ve->lhvc_config->config->nal_unit_size;
	return 0;
}

