static const char* ConvertOneFloat(PyObject* v, T* out) {
  if (PyErr_Occurred()) {
    return nullptr;
  }
  if (TF_PREDICT_TRUE(PyFloat_Check(v))) {
    const double as_double = PyFloat_AS_DOUBLE(v);
    *out = static_cast<T>(as_double);
    if (TF_PREDICT_FALSE(sizeof(T) < sizeof(double) && std::isinf(*out) &&
                         std::isfinite(as_double))) {
      return ErrorOutOfRangeDouble;
    }
    return nullptr;
  }
#if PY_MAJOR_VERSION < 3
  if (PyInt_Check(v)) {
    *out = PyInt_AS_LONG(v);
    return nullptr;
  }
#endif
  if (PyLong_Check(v)) {
    *out = PyLong_AsDouble(v);
    if (PyErr_Occurred()) return ErrorOutOfRangeDouble;
    return nullptr;
  }
  if (PyIsInstance(v, &PyFloatingArrType_Type)) {  
    Safe_PyObjectPtr as_float = make_safe(PyNumber_Float(v));
    if (PyErr_Occurred()) {
      return nullptr;
    }
    return ConvertOneFloat<T>(as_float.get(), out);
  }
  if (PyIsInstance(v, &PyIntegerArrType_Type)) {  
#if PY_MAJOR_VERSION < 3
    Safe_PyObjectPtr as_int = make_safe(PyNumber_Int(v));
#else
    Safe_PyObjectPtr as_int = make_safe(PyNumber_Long(v));
#endif
    if (PyErr_Occurred()) {
      return nullptr;
    }
    return ConvertOneFloat<T>(as_int.get(), out);
  }
  return ErrorMixedTypes;
}

StreamBufferHandle_t xStreamBufferGenericCreate( size_t xBufferSizeBytes,
                                                 size_t xTriggerLevelBytes,
                                                 BaseType_t xIsMessageBuffer )
{
    uint8_t * pucAllocatedMemory;
    uint8_t ucFlags;
    if( xIsMessageBuffer == pdTRUE )
    {
        ucFlags = sbFLAGS_IS_MESSAGE_BUFFER;
        configASSERT( xBufferSizeBytes > sbBYTES_TO_STORE_MESSAGE_LENGTH );
    }
    else
    {
        ucFlags = 0;
        configASSERT( xBufferSizeBytes > 0 );
    }
    configASSERT( xTriggerLevelBytes <= xBufferSizeBytes );
    if( xTriggerLevelBytes == ( size_t ) 0 )
    {
        xTriggerLevelBytes = ( size_t ) 1;
    }
    xBufferSizeBytes++;
    pucAllocatedMemory = ( uint8_t * ) pvPortMalloc( xBufferSizeBytes + sizeof( StreamBuffer_t ) ); 
    if( pucAllocatedMemory != NULL )
    {
        prvInitialiseNewStreamBuffer( ( StreamBuffer_t * ) pucAllocatedMemory,         
                                      pucAllocatedMemory + sizeof( StreamBuffer_t ),  
                                      xBufferSizeBytes,
                                      xTriggerLevelBytes,
                                      ucFlags );
        traceSTREAM_BUFFER_CREATE( ( ( StreamBuffer_t * ) pucAllocatedMemory ), xIsMessageBuffer );
    }
    else
    {
        traceSTREAM_BUFFER_CREATE_FAILED( xIsMessageBuffer );
    }
    return ( StreamBufferHandle_t ) pucAllocatedMemory; 
}

static int setup_ttydir_console(const struct lxc_rootfs *rootfs,
			 const struct lxc_console *console,
			 char *ttydir)
{
	char path[MAXPATHLEN], lxcpath[MAXPATHLEN];
	int ret;
	ret = snprintf(path, sizeof(path), "%s/dev/%s", rootfs->mount,
		       ttydir);
	if (ret >= sizeof(path))
		return -1;
	ret = mkdir(path, 0755);
	if (ret && errno != EEXIST) {
		SYSERROR("failed with errno %d to create %s", errno, path);
		return -1;
	}
	INFO("created %s", path);
	ret = snprintf(lxcpath, sizeof(lxcpath), "%s/dev/%s/console",
		       rootfs->mount, ttydir);
	if (ret >= sizeof(lxcpath)) {
		ERROR("console path too long");
		return -1;
	}
	snprintf(path, sizeof(path), "%s/dev/console", rootfs->mount);
	ret = unlink(path);
	if (ret && errno != ENOENT) {
		SYSERROR("error unlinking %s", path);
		return -1;
	}
	ret = creat(lxcpath, 0660);
	if (ret==-1 && errno != EEXIST) {
		SYSERROR("error %d creating %s", errno, lxcpath);
		return -1;
	}
	if (ret >= 0)
		close(ret);
	if (console->master < 0) {
		INFO("no console");
		return 0;
	}
	if (mount(console->name, lxcpath, "none", MS_BIND, 0)) {
		ERROR("failed to mount '%s' on '%s'", console->name, lxcpath);
		return -1;
	}
	ret = snprintf(lxcpath, sizeof(lxcpath), "%s/console", ttydir);
	if (ret >= sizeof(lxcpath)) {
		ERROR("lxc/console path too long");
		return -1;
	}
	ret = symlink(lxcpath, path);
	if (ret) {
		SYSERROR("failed to create symlink for console");
		return -1;
	}
	INFO("console has been setup on %s", lxcpath);
	return 0;
}

static struct nlattr *reserve_sfa_size(struct sw_flow_actions **sfa,
				       int attr_len, bool log)
{
	struct sw_flow_actions *acts;
	int new_acts_size;
	size_t req_size = NLA_ALIGN(attr_len);
	int next_offset = offsetof(struct sw_flow_actions, actions) +
					(*sfa)->actions_len;
	if (req_size <= (ksize(*sfa) - next_offset))
		goto out;
	new_acts_size = max(next_offset + req_size, ksize(*sfa) * 2);
	if (new_acts_size > MAX_ACTIONS_BUFSIZE) {
		if ((MAX_ACTIONS_BUFSIZE - next_offset) < req_size) {
			OVS_NLERR(log, "Flow action size exceeds max %u",
				  MAX_ACTIONS_BUFSIZE);
			return ERR_PTR(-EMSGSIZE);
		}
		new_acts_size = MAX_ACTIONS_BUFSIZE;
	}
	acts = nla_alloc_flow_actions(new_acts_size);
	if (IS_ERR(acts))
		return (void *)acts;
	memcpy(acts->actions, (*sfa)->actions, (*sfa)->actions_len);
	acts->actions_len = (*sfa)->actions_len;
	acts->orig_len = (*sfa)->orig_len;
	kfree(*sfa);
	*sfa = acts;
out:
	(*sfa)->actions_len += req_size;
	return  (struct nlattr *) ((unsigned char *)(*sfa) + next_offset);
}

static int kvm_ioctl_create_device(struct kvm *kvm,
				   struct kvm_create_device *cd)
{
	struct kvm_device_ops *ops = NULL;
	struct kvm_device *dev;
	bool test = cd->flags & KVM_CREATE_DEVICE_TEST;
	int ret;
	if (cd->type >= ARRAY_SIZE(kvm_device_ops_table))
		return -ENODEV;
	ops = kvm_device_ops_table[cd->type];
	if (ops == NULL)
		return -ENODEV;
	if (test)
		return 0;
	dev = kzalloc(sizeof(*dev), GFP_KERNEL);
	if (!dev)
		return -ENOMEM;
	dev->ops = ops;
	dev->kvm = kvm;
	mutex_lock(&kvm->lock);
	ret = ops->create(dev, cd->type);
	if (ret < 0) {
		mutex_unlock(&kvm->lock);
		kfree(dev);
		return ret;
	}
	list_add(&dev->vm_node, &kvm->devices);
	mutex_unlock(&kvm->lock);
 	if (ops->init)
 		ops->init(dev);
 	ret = anon_inode_getfd(ops->name, &kvm_device_fops, dev, O_RDWR | O_CLOEXEC);
 	if (ret < 0) {
 		mutex_lock(&kvm->lock);
 		list_del(&dev->vm_node);
 		mutex_unlock(&kvm->lock);
 		ops->destroy(dev);
 		return ret;
 	}
	kvm_get_kvm(kvm);
 	cd->fd = ret;
 	return 0;
 }

nsPluginInstance::setupCookies(const std::string& pageurl)
{
    std::string::size_type pos;
    pos = pageurl.find("/", pageurl.find("
    std::string url = pageurl.substr(0, pos);
    std::string ncookie;
    char *cookie = 0;
    uint32_t length = 0;
    NPError rv = NPERR_GENERIC_ERROR;
#if NPAPI_VERSION != 190
    if (NPNFuncs.getvalueforurl) {
        rv = NPN_GetValueForURL(_instance, NPNURLVCookie, url.c_str(),
                                &cookie, &length);
    } else {
        LOG_ONCE( gnash::log_debug("Browser doesn't support getvalueforurl") );
    }
#endif
    if (rv == NPERR_GENERIC_ERROR) {
        log_debug("Trying window.document.cookie for cookies");
        ncookie = getDocumentProp("cookie");
    }
    if (cookie) {
        ncookie.assign(cookie, length);
        NPN_MemFree(cookie);
    }
    if (ncookie.empty()) {
        gnash::log_debug("No stored Cookie for %s", url);
        return;
    }
    gnash::log_debug("The Cookie for %s is %s", url, ncookie);
    std::ofstream cookiefile;
    std::stringstream ss;
     ss << "/tmp/gnash-cookies." << getpid();
     cookiefile.open(ss.str().c_str(), std::ios::out | std::ios::trunc);
    typedef boost::char_separator<char> char_sep;
    typedef boost::tokenizer<char_sep> tokenizer;
    tokenizer tok(ncookie, char_sep(";"));
    for (tokenizer::iterator it=tok.begin(); it != tok.end(); ++it) {
        cookiefile << "Set-Cookie: " << *it << std::endl;
    }
    cookiefile.close();
    if (setenv("GNASH_COOKIES_IN", ss.str().c_str(), 1) < 0) {
        gnash::log_error(
            "Couldn't set environment variable GNASH_COOKIES_IN to %s",
            ncookie);
    }
}

void Compute(OpKernelContext* context) override {
  const auto& input = context->input(0);
  auto flat_in = input.flat<tstring>();
  int fixed_length;
  const auto& length_input = context->input(1);
  OP_REQUIRES(context, TensorShapeUtils::IsScalar(length_input.shape()),
              errors::InvalidArgument("k must be scalar, got shape ",
                                      length_input.shape().DebugString()));
  fixed_length = length_input.scalar<int32>()();
  OP_REQUIRES(
      context, fixed_length % sizeof(T) == 0,
      errors::InvalidArgument(
          "fixed_length (", fixed_length,
          ") must be a multiple of the size of out_type (", sizeof(T), ")"));
  OP_REQUIRES(context, fixed_length > 0,
              errors::InvalidArgument("fixed_length (", fixed_length,
                                      ") must be greater than zero."));
  int width = fixed_length / sizeof(T);
  TensorShape out_shape = input.shape();
  out_shape.AddDim(width);
  Tensor* output_tensor = nullptr;
  OP_REQUIRES_OK(
      context, context->allocate_output("output", out_shape, &output_tensor));
  if (flat_in.size() == 0) {  
    return;
  }
  auto out = output_tensor->flat_inner_dims<T>();
  T* out_data = out.data();
  memset(out_data, 0, fixed_length * flat_in.size());
  if (!convert_data_endianness_ || sizeof(T) == 1) {
    for (int64 i = 0; i < flat_in.size(); ++i) {
      const T* in_data = reinterpret_cast<const T*>(flat_in(i).data());
      if (flat_in(i).size() > fixed_length) {
        memcpy(out_data, in_data, fixed_length);
      } else {
        memcpy(out_data, in_data, flat_in(i).size());
      }
      out_data += fixed_length;
    }
  } else {
    for (int64 i = 0; i < flat_in.size(); ++i) {
      const char* in_data_bytes =
          reinterpret_cast<const char*>(flat_in(i).data());
      char* out_data_bytes = reinterpret_cast<char*>(out_data);
      const char* p_in = in_data_bytes;
      char* p_out = out_data_bytes;
      for (; p_in < in_data_bytes + fixed_length;
           p_in += sizeof(T), p_out += sizeof(T)) {
        std::reverse_copy(p_in, p_in + sizeof(T), p_out);
      }
      out_data += fixed_length;
    }
  }
}

