ImagingPcdDecode(Imaging im, ImagingCodecState state, UINT8* buf, int bytes)
{
    int x;
    int chunk;
    UINT8* out;
    UINT8* ptr;
    ptr = buf;
    chunk = 3 * state->xsize;
    for (;;) {
	if (bytes < chunk)
	    return ptr - buf;
	out = state->buffer;
	for (x = 0; x < state->xsize; x++) {
	    out[0] = ptr[x];
	    out[1] = ptr[(x+4*state->xsize)/2];
	    out[2] = ptr[(x+5*state->xsize)/2];
	    out += 4;
	}
	state->shuffle((UINT8*) im->image[state->y],
		       state->buffer, state->xsize);
	if (++state->y >= state->ysize)
	    return -1; 
	out = state->buffer;
	for (x = 0; x < state->xsize; x++) {
	    out[0] = ptr[x+state->xsize];
	    out[1] = ptr[(x+4*state->xsize)/2];
	    out[2] = ptr[(x+5*state->xsize)/2];
	    out += 4;
	}
	state->shuffle((UINT8*) im->image[state->y],
		       state->buffer, state->xsize);
	if (++state->y >= state->ysize)
	    return -1;
	ptr += chunk;
	bytes -= chunk;
    }
}

 unsigned char *base64decode(const char *buf, size_t *size)
 {
 	if (!buf || !size) return NULL;
	size_t len = (*size > 0) ? *size : strlen(buf);
	if (len <= 0) return NULL;
 	unsigned char *outbuf = (unsigned char*)malloc((len/4)*3+3);
 	const char *ptr = buf;
 	int p = 0;
	size_t l = 0;
 	do {
		ptr += strspn(ptr, "\r\n\t ");
 		if (*ptr == '\0' || ptr >= buf+len) {
 			break;
 		}
		l = strcspn(ptr, "\r\n\t ");
		if (l > 3 && ptr+l <= buf+len) {
			p+=base64decode_block(outbuf+p, ptr, l);
			ptr += l;
		} else {
			break;
 		}
 	} while (1);
	outbuf[p] = 0;
	*size = p;
	return outbuf;
}

